<template>
  <q-bar >
    <div class="q-pa-sm q-pl-md  items-center text-body1">
          <div class="q-ml-md cursor-pointer non-selectable">
            <q-item clickable dense replace v-ripple
              :class="$q.dark.isActive ? 'bg-dark text-white' : 'bg-primary text-white'"  >
              <q-item-section avatar @click="$router.push({ name: 'home'})" >
                <q-icon  name="home" />
              </q-item-section>
              <q-item-section class="text-body1" @click="$router.push({ name: 'home'})" >{{ tdc('Casa') }}</q-item-section>
              <q-item-section side>
                <q-btn
                round dense flat
                :icon="'settings'" :class="$q.dark.isActive ? 'text-white' : 'text-white'"
                @click="User.toggleSettings()"
              >
                <q-tooltip :class="$q.dark.isActive ? 'bg-dark text-white' : 'bg-primary text-white'">{{ tdc('Configurações') }}</q-tooltip>
              </q-btn>
              </q-item-section>
            </q-item>
          </div>
        </div>
    <TopMenuSegundo :AllPermissions="AllPermissions"></TopMenuSegundo>
  </q-bar>
</template>
<script >

import TopMenuSegundo from './TopMenuSegundo.vue'
import { defineComponent } from 'vue'
import { tdc } from '../boot/app'
import { HTTPAuth, url } from '../boot/api'
import { UserStore } from '../stores/AuthStore'

export default defineComponent({
  components: {
    TopMenuSegundo
  },
  setup () {
    const User = UserStore()
    return {
      User
    }
  },

  data () {
    return {
      tdc: tdc,
      AllPermissions: [],
      optionsAllPermissions: []
    }
  },

  created () {
  },
  computed: {
  },

  mounted () {
    this.setAllPermission()
  },

  methods: {
    async setAllPermission () {
      await HTTPAuth.get(url({ type: 'u', url: 'auth/permissions/', params: {} }))
        .then(res => {
          this.AllPermissions = res.data
          this.optionsAllPermissions = this.AllPermissions
          this.Apps = res.data.reduce((acc, item) => {
            if (!acc[item.content_type_app]) {
              acc[item.content_type_app] = []
            }
            acc[item.content_type_app].push(item)
            return acc
          }, {})
        }).catch(err => {
          console.log(err)
        })
    }
  }
})
</script>
